menu.configs.handle.load_config = function(item, last_used)
    item = item + 1

    if not menu.configs.list[item] then
        script_db.handlers.handle_error({
            { 'You didn\'t ' },
            { 'select', true },
            { ' any config' }
        })
        return
    end

    local config = script_db.database.configs[item]

    local name = menu.configs.list[item]

    if name:find('default config') ~= nil then
        name = 'default config'
    end

    if item == 1 then
        menu.configs.last_loaded_config = 0

        if last_used then
            local message = {
                text = {
                    { 'Succesfully ' },
                    { 'loaded', true },
                    { ' your ' },
                    { 'last used', true },
                    { ' config [' },
                    { name, true },
                    { ']' }
                },
                error_text = {
                    { 'Error while ' },
                    { 'loading', true },
                    { ' your ' },
                    { 'last used', true },
                    { ' config [' },
                    { name, true },
                    { ']' }
                }
            }

            configs.handle.import(script_db.configs.default, nil, message.text, message.error_text)
            return
        end

        local message = {
            text = {
                { 'Succesfully ' },
                { 'loaded', true },
                { ' default settings' }
            },
            error_text = {
                { 'Error while ' },
                { 'loading', true },
                { ' default settings' }
            }
        }

        configs.handle.import(script_db.configs.default, nil, message.text, message.error_text)
        return
    end

    if config == nil or config == '' then
        script_db.handlers.handle_error({
            { 'This config [' },
            { name, true },
            { '] is ' },
            { 'blank', true }
        })
        return
    end

    local protected = function()
        configs.import(config)

        if last_used then
            ui.set(menu.configs.menu.config_list, item - 1)
        end
    end

    local status, error = pcall(protected)

    local message = {
        text = {
            { 'Succesfully ' },
            { 'loaded', true },
            { ' config [' },
            { name, true },
            { ']' }
        },
        error_text = {
            { 'Error while ' },
            { 'loading', true },
            { ' config [' },
            { name, true },
            { ']' }
        }
    }

    if last_used then
        message = {
            text = {
                { 'Succesfully ' },
                { 'loaded', true },
                { ' your ' },
                { 'last used', true },
                { ' config [' },
                { name, true },
                { ']' }
            },
            error_text = {
                { 'Error while ' },
                { 'loading', true },
                { ' your ' },
                { 'last used', true },
                { ' config [' },
                { name, true },
                { ']' }
            }
        }
    end

    if status then
        menu.configs.last_loaded_config = item - 1

        notifier.add({
            time = notifier.time.long,
            text = message.text
        })

        script_db.handlers.console_message(script_db.get_accent_color(), message.text)
    else
        script_db.handlers.handle_error(message.error_text)
    end
end

