            end

            if recharge_hide > .6 then
                if defensive_charged then
                    second_text = spacing .. ready_text_final
                else
                    second_text = spacing .. active_text_final
                end
            else
                second_text = spacing .. recharge_text_final
            end

            if recharge_hide > .1 then
